import * as R from 'ramda';
import { capfirst } from "./";
import { catalogHierarchy } from "../../../settings"; // Returns product's gender, category and subcategory based on its tags

export const productPath = tags => {
  const gender = (R.find(tag => R.includes(tag.toLowerCase(), R.keys(catalogHierarchy)), tags) || '').toLowerCase();
  if (!gender) return {};
  const category = (R.find(tag => R.includes(tag && tag.toLowerCase(), R.pluck('title')(catalogHierarchy[gender].categories)), tags) || '').toLowerCase();
  const categoryObj = catalogHierarchy[gender] ? R.find(R.propEq('title', category), catalogHierarchy[gender].categories) : {
    subcategories: []
  };
  const subcategory = categoryObj ? (R.find(tag => R.includes(tag.toLowerCase(), R.compose(R.flatten, R.pluck('tags'))(categoryObj.subcategories)), tags) || '').toLowerCase() : '';
  return {
    category: capfirst(category),
    gender: capfirst(gender),
    subcategory: capfirst(subcategory)
  };
};
