import * as R from 'ramda';
import { catalogHierarchy } from "../../../settings";
export function findGenderInProductTags(tags) {
  const productTags = R.map(R.toLower, tags || []);
  if (R.includes('women', productTags)) return 'women';
  if (R.includes('men', productTags)) return 'men';
  const menTags = R.compose(R.map(R.toLower), R.filter(Boolean), R.chain(R.prop('tags')), R.chain(R.prop('subcategories')))(catalogHierarchy.men.categories);
  if (R.length(R.intersection(productTags, menTags)) > 0) return 'men';
  return 'women';
}
